{% block title %}Demande de réinitialisation du Mot de Passe{% endblock %}

{% block body %}
    <main aria-label=\"contenu principal\" id=\"main\" class=\"my-auto mx-md-auto\">
        <div class=\"row justify-content-center align-items-center\">
            <div class=\"col-md-6 col-sm-8 pb-sm-5 px-md-5\">
                <h2>Demande de réinitialisation de mot de passe</h2>
                {{ form_start(form,  { attr: { novalidate: 'novalidate', aria_label:'Formulaire de demande de réinitialisation de mot de passe'}}) }}
                    {{ form_label(form.email, 'Email :', { label_attr: { class:'form-label'} }) }}
                    {{ form_widget(form.email, { attr: { class:'form-control', aria_label:'Veuillez obligatoirement saisir votre email'} }) }}
                    {{ form_errors(form.email, { attr: { class:'text-danger'} }) }}
                                        
                    <button type=\"submit\" class=\"btn btn-secondary button btn-submit\">Envoyer</button>
                {{form_end(form)}}
            </div>
            <div class=\"col-md-4 col-sm-6 mt-sm-5 px-md-5\">
                <img src=\"{{ asset('img/mot-passe-oublie.webp') }}\" class=\"img-fluid illustration\" alt=\"image d'illustration du mot de passe oublié\"> 
            </div>
        </div>
    </main>   
{% endblock %}
